<!-- Progress bar -->
  <article>
<h4>Progress bar</h4>
<script>
  $( function() {
    $( ".selector" ).progressbar({
  classes: {
    "ui-progressbar": "ubar",
    "ui-progressbar-value" : "uvalue",
    "ui-progressbar-overlay" : "uover"
  }
});
    var progressTimer,
      progressbar = $( "#progressbar" ),
      progressLabel = $( ".progress-label" ),
      dialogButtons = [{
        text: "Cancel Download",
        click: closeDownload
      }],
      dialog = $( "#dialog" ).dialog({
        autoOpen: false,
        closeOnEscape: false,
        resizable: false,
        buttons: dialogButtons,
        open: function() {
          progressTimer = setTimeout( progress, 2000 );
        },
        beforeClose: function() {
          downloadButton.button( "option", {
            disabled: false,
            label: "Start Download"
          });
        }
      }),
      downloadButton = $( "#downloadButton" )
        .button()
        .on( "click", function() {
          $( this ).button( "option", {
            disabled: true,
            label: "Downloading..."
          });
          dialog.dialog( "open" );
        });
 
    progressbar.progressbar({
      value: false,
      change: function() {
        progressLabel.text( "Current Progress: " + progressbar.progressbar( "value" ) + "%" );
      },
      complete: function() {
        progressLabel.text( "Complete!" );
        dialog.dialog( "option", "buttons", [{
          text: "Close",
          click: closeDownload
        }]);
        $(".ui-dialog button").last().trigger( "focus" );
      }
    });
 
    function progress() {
      var val = progressbar.progressbar( "value" ) || 0;
 
      progressbar.progressbar( "value", val + Math.floor( Math.random() * 3 ) );
      progressbar.progressbar({
  classes: {
    "ui-progressbar": "highlight"
  }
});
 
      if ( val <= 99 ) {
        progressTimer = setTimeout( progress, 50 );
      }
    }
 
    function closeDownload() {
      clearTimeout( progressTimer );
      dialog
        .dialog( "option", "buttons", dialogButtons )
        .dialog( "close" );
      progressbar.progressbar( "value", false );
      progressLabel
        .text( "Starting download..." );
      downloadButton.trigger( "focus" );
    }
  } );
  </script>
  <style>
  #progressbar {
    margin-top: 20px;
    height: 10px;
  }
  .ubar{background:goldenrod;border: 1px dotted goldenrod;}
  .uvalue{background:rgb(147, 113, 28);border: 1px dotted goldenrod;}
  .uover{background:rgba(82, 63, 16, 0.663);border: 1px dotted goldenrod;}
  
  .highlight-progress { background:goldenrod;border: 1px dotted goldenrod;}
  .progress-label {
    font-weight: bold;
    text-shadow: 1px 1px 0 #7fc6dc;
  }
 
  .ui-dialog-titlebar-close {
    display: none;
  }
  </style>
<div id="dialog" title="File Download">
  <div class="progress-label">Starting download...</div>
  <div id="progressbar"></div>
</div>
